package study;

public class Study190913_1 {
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		System.out.println("현재의 a는 " + a + "입니다.");
		a++;
		System.out.println("현재의 a는 " + a + "입니다.");
		System.out.println("현재의 a는 " + ++a + "입니다.");
		System.out.println("현재의 a는 " + a++ + "입니다.");
		System.out.println("현재의 a는 " + a + "입니다.");
	}

}
