package study;

public class Study190914_1 {
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int score = 95;
		
		if(score >= 90) {
			System.out.println("A+입니다.");
		}
		else if(score >= 80) {
			System.out.println("B+입니다.");
		}
		else if(score >= 70) {
			System.out.println("C+입니다.");
		}
		else {
			System.out.println("F입니다.");
		}
	}

}
