package study;

public class Study190914_4 {
	
	final static int N = 30;
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// for문 : 초기화 부분, 조건 부분, 연산 부분
		for(int i=N; i>0; i--) {
			for(int j=i; j>0;j--) {
				System.out.print("*");
			}
			System.out.println();
		}
		
	}

}
